                element.addEventListener('click', function(e) {
                    e.preventDefault();
                    var parent = this.closest('.MCDropDown');
                    if (parent.classList.contains('MCDropDown_Closed')) {
                        parent.classList.remove('MCDropDown_Closed');
                        parent.classList.add('MCDropDown_Open');
